import React from 'react';
import { StatusBar, ScrollView} from 'react-native';
import styled from 'styled-components';
import BigCard from "../components/BigCard";
import MedCard from '../components/MedCard';
import {Ionicons } from "@expo/vector-icons" 
import { TouchableOpacity } from "react-native";
import Menu from '../components/Menu';
import { connect } from "react-redux";


function mapStateToProps(state) {
  
  return { menu: state.menu };

}

function mapDispatchToProps(dispatch) {

   return { 

       openMenu: () =>

        dispatch({

          type: "OPENMENU"


       })

   };

}


class HomeScreen extends React.Component {

  state = {

    left:10

  };

  render() {

  return (

    <Main>

    <Menu />

    <ScrollView showsVerticalScrollIndicator={false}>

         <StatusBar hidden />
  
         <Header>

         <TouchableOpacity 

         onPress={this.props.openMenu} 

              style={{
               
                top:12,

                left: this.state.left,
                 
                position:"absolute",

                zIndex: 100
               
            }} 
        >

             <Ionicons

              name="ios-menu"

              size={35}

              color="grey"


                 />

         </TouchableOpacity>        
        
            <Profile />

            <Logo>Sanswet</Logo>

         </Header>
          
        <BigCardContainer>

         <ScrollView horizontal={true} showsHorizontalScrollIndicator={false}>

         {
           BigCardData.map((data,index) => {

             return <BigCard key={index} image={data.image} />;
           })
         }
          
         </ScrollView> 

        </BigCardContainer>

        <ContinueText>Continue Watching</ContinueText>
        
         <MedCardContainer>

         <ScrollView  horizontal={true}  showsHorizontalScrollIndicator={false}>

         {
           MedCardData.map((data,index) => {


             return <MedCard key={index} image={data.image}/>;

           })


         }
      


         </ScrollView>

         </MedCardContainer>

        </ScrollView>
    
     </Main>
   );
 }

}

export default connect(mapStateToProps, mapDispatchToProps)(HomeScreen);

const Main = styled.View`
  
  flex: 1;

  background-color: #efefef;


`;

const Header = styled.View`

 width: 100%;

 height: 56px;
 
 background:#FFFFFF;


`;

const Logo = styled.Text`

 margin-top: 13px;

 margin-left: 50px;

 color: black;

 font-size: 29px;

`;

const Profile = styled.Image`

 position: absolute;

 top: 8px;

 right: 5px;

 width: 40px;

 height: 40px;
 
 background: #c4c4c4;
 
 border-radius: 22px;

`;

const BigCardContainer = styled.View`

 margin-top: 20px;
 
 
 `;


 const ContinueText = styled.Text`
 
  margin-top: 20px;

  margin-left:10px;

  color: #b4b4b4;

  font-size: 15px;

  font-weight: 600;

  text-transform: uppercase;
 
 `;


 const MedCardContainer = styled.View`
 
  margin-top: 20px;
 
 
 `;

 const BigCardData = [
  {
    image: "https://upload.wikimedia.org/wikipedia/en/8/85/Bigg_Boss_Tamil_3.jpg"
  },

 {
    image: "https://i1.ytimg.com/vi/-mLcC7nnfwE/maxresdefault.jpg"
 },

 {
    image: "https://c.ndtvimg.com/2019-10/ra39il58_bigg-boss-tamil-3-grand-finale-_625x300_07_October_19.jpg"
 },
 
 {
   image: "https://c.ndtvimg.com/2019-10/ra39il58_bigg-boss-tamil-3-grand-finale-_625x300_07_October_19.jpg"
 }
]
 

 const  MedCardData = [
  {
    image: require("../assets/1.jpg")
  },

 {
    image: require("../assets/2.jpg")
 },

 { 
    image: require("../assets/3.jpg")
    
 },
 
 {
   image: require("../assets/4.jpg")
 }
]
