import React from "react";

import styled from "styled-components";

import { LinearGradient } from "expo-linear-gradient";

import {Ionicons } from "@expo/vector-icons" 

import { Animated, TouchableOpacity } from "react-native";


export default class Menu extends React.Component{

    state = {

        top: new Animated.Value(900)
    };

     componentDidMount() {

         Animated.spring(this.state.top, { toValue: 0 }).start();

     }

     closeMenu=() => {

        Animated.spring(this.state.top, { toValue: 900}).start();
     }
     

    render() {

        return(

            <AnimatedContainer style={{ position:"absolute", top: this.state.top,zindex:100 }}>
             
             <Cover>
                  
               <LinearGradient

                 colors={[ "rgba(255,148,115,100)", "rgba(255,123,123,100)" ]}

                 style={{width: "100%", height: "100%"}} 

                 />

             </Cover>

            <TouchableOpacity
            
             style={{

                position: "absolute",

                top: 120,

                left: "50%",

                marginLeft: -22

             }}

             onPress={this.closeMenu}
            
            >

              <CloseView>

                  <Ionicons name="ios-close" size={35} color="blue" />

              </CloseView>

            </TouchableOpacity>

             <Content />
        
            </AnimatedContainer>
        )
    }
}



const Container = styled.View`

  position: absolute;

  width: 100%;

  height: 100%;

  background: #f0f3f5;

  z-index:100;
 
 `;


const AnimatedContainer = Animated.createAnimatedComponent(Container);


const Cover = styled.View`

 position: absolute;

 width: 100%;

 height: 144px;

`;

const Content = styled.View``;


const CloseView = styled.View`

  width:  44px;

  height: 44px;

  border-radius: 40px;

  background: white;

  justify-content: center;

  align-items: center;

`;
